import{_ as a,z as l,m as o,ap as r}from"./chunks/framework.40Eg4YuW.js";const q=JSON.parse('{"title":"常见问题","description":"","frontmatter":{},"headers":[],"relativePath":"markdown/FAQ.md","filePath":"markdown/FAQ.md","lastUpdated":1760776675000}'),t={name:"markdown/FAQ.md"};function e(n,i,s,d,h,c){return o(),l("div",null,i[0]||(i[0]=[r('<h1 id="常见问题" tabindex="-1">常见问题 <a class="header-anchor" href="#常见问题" aria-label="Permalink to &quot;常见问题&quot;">​</a></h1><h2 id="安装和配置" tabindex="-1">安装和配置 <a class="header-anchor" href="#安装和配置" aria-label="Permalink to &quot;安装和配置&quot;">​</a></h2><h3 id="q-如何获取-bilibili-uid" tabindex="-1">Q: 如何获取 Bilibili UID？ <a class="header-anchor" href="#q-如何获取-bilibili-uid" aria-label="Permalink to &quot;Q: 如何获取 Bilibili UID？&quot;">​</a></h3><p>A: 有以下几种方法：</p><ol><li><strong>手机端</strong>：进入个人主页 → 查看资料 → 查看UID</li><li><strong>WEB端</strong>：进入个人主页 → 查看地址栏 <code>/</code> 后的数字</li></ol><h3 id="q-扫码登录失败怎么办" tabindex="-1">Q: 扫码登录失败怎么办？ <a class="header-anchor" href="#q-扫码登录失败怎么办" aria-label="Permalink to &quot;Q: 扫码登录失败怎么办？&quot;">​</a></h3><p>A: 请检查：</p><ol><li>确保使用最新版本的 Bilibili APP</li><li>网络连接是否正常</li><li>二维码是否已过期（通常 2 分钟过期）</li><li>尝试刷新页面重新生成二维码</li></ol><h3 id="q-插件安装后无法启动" tabindex="-1">Q: 插件安装后无法启动？ <a class="header-anchor" href="#q-插件安装后无法启动" aria-label="Permalink to &quot;Q: 插件安装后无法启动？&quot;">​</a></h3><p>A: 可能的原因：</p><ol><li><strong>依赖问题</strong>：确保 Koishi 版本 &gt;= 4.18.0</li><li><strong>配置错误</strong>：检查 UID 是否正确填写</li><li><strong>网络问题</strong>：确保能正常访问 Bilibili API</li><li><strong>权限问题</strong>：确保 Koishi 有足够的文件读写权限</li></ol><h2 id="使用问题" tabindex="-1">使用问题 <a class="header-anchor" href="#使用问题" aria-label="Permalink to &quot;使用问题&quot;">​</a></h2><h3 id="q-为什么消息发送-接收有延迟" tabindex="-1">Q: 为什么消息发送/接收有延迟？ <a class="header-anchor" href="#q-为什么消息发送-接收有延迟" aria-label="Permalink to &quot;Q: 为什么消息发送/接收有延迟？&quot;">​</a></h3><p>A: 这是正常现象，原因包括：</p><ol><li><strong>B站限制</strong>：为防止滥用，B站对私信 API 有频率限制</li><li><strong>轮询机制</strong>：插件使用轮询方式获取新消息</li><li><strong>网络延迟</strong>：网络环境可能影响响应速度</li></ol><p>通常延迟在 3-30 秒属于正常范围。</p><h3 id="q-头像图片无法显示怎么办" tabindex="-1">Q: 头像图片无法显示怎么办？ <a class="header-anchor" href="#q-头像图片无法显示怎么办" aria-label="Permalink to &quot;Q: 头像图片无法显示怎么办？&quot;">​</a></h3><p>A: 解决方案：</p><ol><li><strong>启用 Base64 转换</strong>：在配置中开启 <code>imageToBase64</code> 选项</li><li><strong>请求头</strong>：请求B站图片时，确保使用<code>no-referrer</code></li><li><strong>浏览器设置</strong>：某些浏览器可能阻止跨域图片</li></ol><h3 id="q-消息发送失败" tabindex="-1">Q: 消息发送失败？ <a class="header-anchor" href="#q-消息发送失败" aria-label="Permalink to &quot;Q: 消息发送失败？&quot;">​</a></h3><p>A: 可能的原因：</p><ol><li><strong>内容被屏蔽</strong>：B站有内容审核机制，敏感内容可能被拦截</li><li><strong>频率限制</strong>：发送过于频繁会被限制</li><li><strong>登录过期</strong>：需要重新扫码登录</li><li><strong>网络问题</strong>：检查网络连接</li></ol><h3 id="q-支持发送哪些类型的消息" tabindex="-1">Q: 支持发送哪些类型的消息？ <a class="header-anchor" href="#q-支持发送哪些类型的消息" aria-label="Permalink to &quot;Q: 支持发送哪些类型的消息？&quot;">​</a></h3><p>A: 目前支持：</p><ul><li>✅ 纯文本消息</li><li>✅ 图片消息（需先上传到 B站）</li><li>❌ 视频消息（暂不支持）</li><li>❌ 语音消息（暂不支持）</li><li>❌ 表情包（部分支持）</li></ul>',25)]))}const p=a(t,[["render",e]]);export{q as __pageData,p as default};
